// src/pages/Login.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import loginImage from '../assets/login-image.jpg'; // Replace with your actual image path

function Login() {
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();
    navigate('/home');
  };

  return (
    <div className="flex h-screen">
      {/* Left Side: Image */}
      <div className="w-1/2 hidden md:block">
        <img
          src={loginImage}
          alt="Login Visual"
          className="w-full h-full object-cover rounded-l-3xl"
        />
      </div>

      {/* Right Side: Login Form */}
      <div className="w-full md:w-1/2 flex items-center justify-center bg-white px-6">
        <div className="max-w-md w-full p-10 shadow-2xl rounded-3xl border">
          <h2 className="text-3xl font-bold text-blue-600 text-center mb-6">Internship Portal</h2>
          <form onSubmit={handleLogin} className="space-y-5">
            <div>
              <label className="block text-gray-700 font-semibold">Name</label>
              <input
                type="text"
                required
                placeholder="Enter your name"
                className="w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>
            <div>
              <label className="block text-gray-700 font-semibold">Email</label>
              <input
                type="email"
                required
                placeholder="Enter your email"
                className="w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>
            <button
              type="submit"
              className="w-full bg-blue-600 text-white py-2 rounded-md hover:bg-blue-700 transition"
            >
              Login
            </button>
            <button
              type="button"
              onClick={() => alert('Redirect to Signup Page')}
              className="w-full bg-gray-100 text-blue-600 py-2 rounded-md hover:bg-gray-200 transition"
            >
              Sign Up
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Login;
