import React, { useState } from "react";
import profilePic from "../assets/profile.png"; // your profile logo
import bgImage from "../assets/bg.jpg"; // background image

const Profile = () => {
  const [profile, setProfile] = useState({
    name: "Sargam Sandip Shirke",
    email: "sargam@example.com",
    phone: "9876543210",
    education: "B.Sc Computer Science - R.J. College, Mumbai",
    skills: "React, Tailwind CSS, JavaScript, Git",
  });

  const handleChange = (e) => {
    setProfile({ ...profile, [e.target.name]: e.target.value });
  };

  const handleCreateResume = () => {
    alert("Resume Created Successfully!");
    // Optionally: send this data to a backend / download as PDF
  };

  return (
    <div
      className="min-h-screen bg-cover bg-center flex items-center justify-center p-4"
      style={{ backgroundImage: `url(${bgImage})` }}
    >
      <div className="bg-white shadow-lg rounded-xl p-8 max-w-lg w-full">
        <div className="flex justify-center mb-6">
          <img src={profilePic} alt="Profile" className="h-24 w-24 rounded-full" />
        </div>
        <h2 className="text-xl font-bold text-center mb-4">Resume Profile</h2>

        <div className="space-y-4">
          <input
            type="text"
            name="name"
            value={profile.name}
            onChange={handleChange}
            placeholder="Name"
            className="w-full p-2 border border-gray-300 rounded"
          />
          <input
            type="email"
            name="email"
            value={profile.email}
            onChange={handleChange}
            placeholder="Email"
            className="w-full p-2 border border-gray-300 rounded"
          />
          <input
            type="tel"
            name="phone"
            value={profile.phone}
            onChange={handleChange}
            placeholder="Phone Number"
            className="w-full p-2 border border-gray-300 rounded"
          />
          <textarea
            name="education"
            value={profile.education}
            onChange={handleChange}
            placeholder="Education"
            className="w-full p-2 border border-gray-300 rounded"
          />
          <textarea
            name="skills"
            value={profile.skills}
            onChange={handleChange}
            placeholder="Skills (comma separated)"
            className="w-full p-2 border border-gray-300 rounded"
          />
        </div>

        <button
          onClick={handleCreateResume}
          className="mt-6 w-full bg-blue-600 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded transition duration-300"
        >
          Create Resume
        </button>
      </div>
    </div>
  );
};

export default Profile;
