import React, { useState } from "react";
import profilePic from "../assets/logo.png"; // Use your image here

const Profile = () => {
  const [profile, setProfile] = useState({
    name: "Sargam Sandip Shirke",
    email: "sargam@example.com",
    phone: "9876543210",
    education: "B.Sc Computer Science - R.J. College, Mumbai",
    skills: "React, Tailwind CSS, JavaScript, Git",
  });

  const handleChange = (e) => {
    setProfile({ ...profile, [e.target.name]: e.target.value });
  };

  const handleUpdateResume = () => {
    alert("Resume Updated Successfully!");
  };

  const skillArray = profile.skills.split(",").map((skill) => skill.trim());

  return (
    <div className="min-h-screen bg-gray-100 flex justify-center items-center p-4">
      <div
        className="bg-white shadow-xl rounded-xl p-6 w-full max-w-3xl"
        style={{
          border: "1px solid #ccc",
        }}
      >
        <h2 className="text-3xl font-bold text-center mb-6 text-blue-700">
          Resume
        </h2>

        <div className="flex flex-col md:flex-row gap-6">
          {/* Profile Image Box */}
          <div className="flex-shrink-0 flex justify-center md:justify-start">
            <img
              src={profilePic}
              alt="Profile"
              className="h-40 w-40 object-cover rounded-lg border border-gray-300"
            />
          </div>

          {/* Editable Fields */}
          <div className="flex-1 space-y-4">
            <div>
              <label className="font-semibold block">Name:</label>
              <input
                type="text"
                name="name"
                value={profile.name}
                onChange={handleChange}
                className="w-full p-2 border border-gray-300 rounded mt-1"
              />
            </div>

            <div>
              <label className="font-semibold block">Email:</label>
              <input
                type="email"
                name="email"
                value={profile.email}
                onChange={handleChange}
                className="w-full p-2 border border-gray-300 rounded mt-1"
              />
            </div>

            <div>
              <label className="font-semibold block">Phone:</label>
              <input
                type="tel"
                name="phone"
                value={profile.phone}
                onChange={handleChange}
                className="w-full p-2 border border-gray-300 rounded mt-1"
              />
            </div>

            <div>
              <label className="font-semibold block">Education:</label>
              <textarea
                name="education"
                value={profile.education}
                onChange={handleChange}
                className="w-full p-2 border border-gray-300 rounded mt-1"
              />
            </div>

            <div>
              <label className="font-semibold block mb-1">Skills:</label>
              <div className="flex flex-wrap gap-2 mb-2">
                {skillArray.map((skill, idx) => (
                  <span
                    key={idx}
                    className="bg-blue-100 text-blue-800 px-3 py-1 rounded-full text-sm"
                  >
                    {skill}
                  </span>
                ))}
              </div>
              <textarea
                name="skills"
                value={profile.skills}
                onChange={handleChange}
                placeholder="Skills (comma separated)"
                className="w-full p-2 border border-gray-300 rounded"
              />
            </div>
          </div>
        </div>

        {/* Update Resume Button */}
        <button
          onClick={handleUpdateResume}
          className="mt-6 w-full bg-green-600 hover:bg-green-700 text-white font-semibold py-2 px-4 rounded transition"
        >
          Update Resume
        </button>
      </div>
    </div>
  );
};

export default Profile;
