import { useNavigate } from 'react-router-dom';
import './Login.css'; // Import extra CSS
import LoginImage from '../assets/login-image.jpg'; // add your image here

export default function Login() {
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();
    alert('Login Successful!');
    navigate('/home');
  };

  return (
    <div className="login-page flex items-center justify-center min-h-screen bg-gradient-to-br from-purple-200 to-blue-200">
      <div className="login-card bg-white rounded-xl shadow-xl overflow-hidden max-w-4xl w-full flex">
        {/* Left: form */}
        <div className="form-pane p-8 flex-1">
          <h2 className="text-3xl font-bold mb-2 text-indigo-600">Internship Portal</h2>
          <p className="mb-6 text-gray-700">Login or Signup to continue</p>
          <form onSubmit={handleLogin} className="space-y-4">
            <div>
              <label className="block text-gray-700 mb-1">Name</label>
              <input type="text" required className="input-field" placeholder="Your Name" />
            </div>
            <div>
              <label className="block text-gray-700 mb-1">Email</label>
              <input type="email" required className="input-field" placeholder="you@example.com" />
            </div>
            <div className="flex gap-4 mt-4">
              <button type="submit" className="btn-primary">Login</button>
              <button type="button" onClick={() => alert('Signup clicked!')} className="btn-secondary">Signup</button>
            </div>
          </form>
        </div>

        {/* Right: image */}
        <div className="image-pane hidden md:flex flex-1">
          <img src={LoginImage} alt="Internship Poster" className="object-cover w-full h-full" />
        </div>
      </div>
    </div>
  );
}
