import { useNavigate } from 'react-router-dom';
import { useState } from 'react';

function Login() {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [pass, setPass] = useState('');
  const handleLogin = () => {
    if (email && pass) {
      alert('Login Successful!');
      navigate('/home');
    }
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gray-100">
      <h1 className="text-3xl font-bold mb-4">Login</h1>
      <input placeholder="Email" onChange={(e) => setEmail(e.target.value)} className="mb-2 p-2 border" />
      <input type="password" placeholder="Password" onChange={(e) => setPass(e.target.value)} className="mb-2 p-2 border" />
      <button onClick={handleLogin} className="bg-blue-500 text-white p-2 rounded">Login</button>
    </div>
  );
}

export default Login;
