import React, { useState } from "react";
import { Link } from "react-router-dom";
import { portfoliologo } from "./../assets";

const Navbar = () => {
  const [toggle, setToggle] = useState(false);

  return (
    <nav className="w-full p-4 flex justify-between items-center bg-[#282c34] text-white relative">
      <img src={portfoliologo} alt="Logo" className="h-[60px]" />

      {/* Desktop Links */}
      <div className="hidden md:flex gap-4">
        <Link to="/" className="hover:text-gray-300 transition">Home</Link>
        <Link to="/internships" className="hover:text-gray-300 transition">Internships</Link>
        <Link to="/apply" className="hover:text-gray-300 transition">Apply</Link>
        <Link to="/about" className="hover:text-gray-300 transition">About</Link>
        <Link to="/contact" className="hover:text-gray-300 transition">Contact</Link>
        <Link to="/login" className="hover:text-gray-300 transition">Login</Link>
        <Link to="/signup" className="hover:text-gray-300 transition">Signup</Link>
      </div>

      {/* Toggle Button */}
      <div className="md:hidden">
        {!toggle ? (
          <svg
            onClick={() => setToggle(true)}
            xmlns="http://www.w3.org/2000/svg"
            className="h-6 w-6 cursor-pointer"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5} d="M4 6h16M4 12h16M4 18h16" />
          </svg>
        ) : (
          <svg
            onClick={() => setToggle(false)}
            xmlns="http://www.w3.org/2000/svg"
            className="h-6 w-6 cursor-pointer"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5} d="M6 18L18 6M6 6l12 12" />
          </svg>
        )}
      </div>

      {/* Mobile Links */}
      {toggle && (
        <div className="absolute top-[70px] right-4 bg-[#333] text-white p-4 rounded-md flex flex-col gap-2 md:hidden z-50">
          <Link to="/" onClick={() => setToggle(false)} className="hover:text-gray-300">Home</Link>
          <Link to="/internships" onClick={() => setToggle(false)} className="hover:text-gray-300">Internships</Link>
          <Link to="/apply" onClick={() => setToggle(false)} className="hover:text-gray-300">Apply</Link>
          <Link to="/about" onClick={() => setToggle(false)} className="hover:text-gray-300">About</Link>
          <Link to="/contact" onClick={() => setToggle(false)} className="hover:text-gray-300">Contact</Link>
          <Link to="/login" onClick={() => setToggle(false)} className="hover:text-gray-300">Login</Link>
          <Link to="/signup" onClick={() => setToggle(false)} className="hover:text-gray-300">Signup</Link>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
