import React, { useState } from "react";
import profilePic from "../assets/logo.png"; // Rectangle image (use square or landscape photo)


const Profile = () => {
  const [profile, setProfile] = useState({
    name: "Sargam Sandip Shirke",
    email: "sargam@example.com",
    phone: "9876543210",
    education: "B.Sc Computer Science - R.J. College, Mumbai",
    skills: "React, Tailwind CSS, JavaScript, Git",
  });

  const handleChange = (e) => {
    setProfile({ ...profile, [e.target.name]: e.target.value });
  };

  const handleCreateResume = () => {
    alert("Resume Created Successfully!");
  };

  return (
    <div
      className="min-h-screen bg-cover bg-center flex items-center justify-center p-6"
      
    >
      <div className="bg-white shadow-2xl rounded-lg p-8 w-full max-w-3xl">
        <h2 className="text-2xl font-bold text-center mb-6 text-gray-800">Resume</h2>

        <div className="flex flex-col md:flex-row gap-6">
          {/* Profile Image Box */}
          <div className="flex-shrink-0 flex justify-center md:justify-start">
            <img
              src={profilePic}
              alt="Profile"
              className="h-40 w-40 object-cover rounded-lg border border-gray-300"
            />
          </div>

          {/* Editable Fields */}
          <div className="flex-1 space-y-4">
            <input
              type="text"
              name="name"
              value={profile.name}
              onChange={handleChange}
              placeholder="Full Name"
              className="w-full p-2 border border-gray-300 rounded"
            />
            <input
              type="email"
              name="email"
              value={profile.email}
              onChange={handleChange}
              placeholder="Email"
              className="w-full p-2 border border-gray-300 rounded"
            />
            <input
              type="tel"
              name="phone"
              value={profile.phone}
              onChange={handleChange}
              placeholder="Phone Number"
              className="w-full p-2 border border-gray-300 rounded"
            />
            <textarea
              name="education"
              value={profile.education}
              onChange={handleChange}
              placeholder="Education"
              className="w-full p-2 border border-gray-300 rounded"
            />
            <textarea
              name="skills"
              value={profile.skills}
              onChange={handleChange}
              placeholder="Skills (comma separated)"
              className="w-full p-2 border border-gray-300 rounded"
            />
          </div>
        </div>

        {/* Create Resume Button */}
        <button
          onClick={handleCreateResume}
          className="mt-6 w-full bg-blue-600 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded transition"
        >
          Create Resume
        </button>
      </div>
    </div>
  );
};

export default Profile;
